import test from "ava";
import {
  CreditCardData,
  GatewayError,
  Logger,
  RecurringSequence,
  RecurringType,
  SampleRequestLogger,
  ServicesContainer,
  TransactionModifier,
} from "../../../../src";
import { GpEcomConfig } from "../../../../src/ServiceConfigs/";

const config = new GpEcomConfig();
config.merchantId = "heartlandgpsandbox";
config.accountId = "api";
config.sharedSecret = "secret";
config.rebatePassword = "rebate";
config.refundPassword = "refund";
config.requestLogger = new SampleRequestLogger(new Logger("logs"));

const card = new CreditCardData();
card.number = "4111111111111111";
card.expMonth = "12";
card.expYear = "2025";
card.cvn = "123";
card.cardHolderName = "Joe Smith";

test.before(() => {
  ServicesContainer.configureService(config);
});

test("credit authorization", async (t) => {
  t.plan(4);

  const authorization = await card
    .authorize(14)
    .withCurrency("USD")
    .withAllowDuplicates(true)
    .execute();

  t.not(authorization, null);
  t.is(authorization.responseCode, "00");

  const capture = await authorization.capture(16).withGratuity(2).execute();

  t.not(capture, null);
  t.is(capture.responseCode, "00");
});

test("credit sale", async (t) => {
  t.plan(2);

  const response = await card
    .charge(15)
    .withCurrency("USD")
    .withAllowDuplicates(true)
    .execute();

  t.truthy(response);
  t.is(response.responseCode, "00", response.responseMessage);
});

test("credit sale with recurring", async (t) => {
  t.plan(2);

  const response = await card
    .charge(15)
    .withCurrency("USD")
    .withRecurringInfo(RecurringType.Fixed, RecurringSequence.First)
    .withAllowDuplicates(true)
    .execute();

  t.truthy(response);
  t.is(response.responseCode, "00", response.responseMessage);
});

test("credit refund", async (t) => {
  t.plan(2);

  const response = await card
    .refund(15)
    .withCurrency("USD")
    .withAllowDuplicates(true)
    .execute();

  t.truthy(response);
  t.is(response.responseCode, "00", response.responseMessage);
});

test("credit rebate", async (t) => {
  t.plan(4);

  const response = await card
    .charge(12)
    .withCurrency("USD")
    .withAllowDuplicates(true)
    .execute();

  t.truthy(response);
  t.is(response.responseCode, "00", response.responseMessage);

  const rebate = await response.refund(12).withCurrency("USD").execute();

  t.truthy(rebate);
  t.is(rebate.responseCode, "00", rebate.responseMessage);
});

test("credit void", async (t) => {
  t.plan(4);

  const response = await card
    .charge(12)
    .withCurrency("USD")
    .withAllowDuplicates(true)
    .execute();

  t.truthy(response);
  t.is(response.responseCode, "00", response.responseMessage);

  const rebate = await response.void().execute();

  t.truthy(rebate);
  t.is(rebate.responseCode, "00", rebate.responseMessage);
});

test("credit verify", async (t) => {
  const response = await card.verify().withAllowDuplicates(true).execute();

  t.truthy(response);
  t.is(response.responseCode, "00", response.responseMessage);
});

